configuration.script-text:
db.rom.files.aggregate([
	{ $group: { _id: null, total_length: { $sum: "$length" } } },
	{
		$addFields: {
			total_size: { $toString: { $toLong: "$total_length" } },
		},
	},
	{
		$project: {
			_id: 0,
			total_length: 1,
			total_size: { $concat: ["$total_size", " ", "Bytes"] },
		},
	},
	{ $limit: 1 },
]);
